CREATE DATABASE IF NOT EXISTS `vueblog`;
USE `vueblog`;

CREATE TABLE IF NOT EXISTS `blog_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `thumbnail` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  `content` varchar(10000) NOT NULL,
  `tags` varchar(255) NOT NULL,
  `publish` date NOT NULL,
  `status` int(11) NOT NULL,
  `author_id` int(11) NOT NULL,
  `category_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `blog_article_author_id_905add38_fk_blog_user_id` (`author_id`),
  KEY `blog_article_category_id_7e38f15e_fk_blog_category_id` (`category_id`)
);

CREATE TABLE IF NOT EXISTS `blog_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`)
);

CREATE TABLE IF NOT EXISTS `blog_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` varchar(500) NOT NULL,
  `publish` date NOT NULL,
  `article_id` int(11) NOT NULL,
  `author_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `blog_comment_article_id_3d58bca6_fk_blog_article_id` (`article_id`),
  KEY `blog_comment_author_id_4f11e2e0_fk_blog_user_id` (`author_id`)
);

CREATE TABLE IF NOT EXISTS `blog_permission` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  `role_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `blog_permission_role_id_e9531d47_fk_blog_role_id` (`role_id`)
);

CREATE TABLE IF NOT EXISTS `blog_role` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
);

CREATE TABLE IF NOT EXISTS `blog_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `last_name` varchar(255) NOT NULL,
  `email` varchar(255) NOT NULL,
  `password` varchar(255) NOT NULL,
  `role_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `email` (`email`),
  KEY `blog_user_role_id_132aa2bb_fk_blog_role_id` (`role_id`)
);

CREATE TABLE IF NOT EXISTS `django_migrations` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `app` varchar(255) NOT NULL,
  `name` varchar(255) NOT NULL,
  `applied` datetime(6) NOT NULL,
  PRIMARY KEY (`id`)
);


ALTER TABLE `blog_article`
  ADD CONSTRAINT `blog_article_author_id_905add38_fk_blog_user_id` FOREIGN KEY (`author_id`) REFERENCES `blog_user` (`id`),
  ADD CONSTRAINT `blog_article_category_id_7e38f15e_fk_blog_category_id` FOREIGN KEY (`category_id`) REFERENCES `blog_category` (`id`);

ALTER TABLE `blog_comment`
  ADD CONSTRAINT `blog_comment_article_id_3d58bca6_fk_blog_article_id` FOREIGN KEY (`article_id`) REFERENCES `blog_article` (`id`),
  ADD CONSTRAINT `blog_comment_author_id_4f11e2e0_fk_blog_user_id` FOREIGN KEY (`author_id`) REFERENCES `blog_user` (`id`);

ALTER TABLE `blog_permission`
  ADD CONSTRAINT `blog_permission_role_id_e9531d47_fk_blog_role_id` FOREIGN KEY (`role_id`) REFERENCES `blog_role` (`id`);

ALTER TABLE `blog_user`
  ADD CONSTRAINT `blog_user_role_id_132aa2bb_fk_blog_role_id` FOREIGN KEY (`role_id`) REFERENCES `blog_role` (`id`);
COMMIT;

